begin transaction;
CREATE temporary TABLE bc_location_dupes (
dupe_obj_id int4 NOT NULL,
CONSTRAINT bc_location_dupes_pk PRIMARY KEY (dupe_obj_id)
);
insert into bc_location_dupes
select objectid from internal.bc_location loc
left join (
SELECT min(objectid) as dupe_obj_id
FROM internal.bc_location
group by location_id
) mins
on loc.objectid = mins.dupe_obj_id
where dupe_obj_id is null
and loc.objectid is not null;
delete from internal.bc_location loc
using bc_location_dupes dup
where loc.objectid = dup.dupe_obj_id;
commit;
